--Exclusions for IPEDS EF 2023-24 Reporting
--Regarding Fall 2022 IPEDS Cohort
--
--Shows if a student is deceased or could not complete due to active duty military service.
--Note: Students serving abroad on religious mission or federal program not recorded.
--Note: Students with total or permanent disability not recorded.

SELECT distinct SPRIDEN_ID STUDENT_ID,SHRDGMR_GRAD_DATE GRADUATION_DATE,

  CASE WHEN SHRDGMR_DEGS_CODE = 'AW' THEN 'GRADUATED' 
  ELSE 'NOT GRADUATED' --Indicates student is graduated or not
  END AS GRADUATED_OR_NOT,
  
  CASE WHEN SPRIDEN_PIDM IN (SELECT SHRTGPA_PIDM
                             FROM SHRTGPA
                             WHERE SHRTGPA_HOURS_ATTEMPTED > 11)
        THEN 'FULL-TIME'
        ELSE 'PART-TIME' 
  END AS ENROLLMENT_STATUS

FROM SPRIDEN,SHRDGMR

WHERE SHRDGMR_PIDM = SPRIDEN_PIDM

AND SHRDGMR_GRAD_DATE <= '15-OCT-23'
